mapper.select:
select r.*, d.* from requests r join dev_repo d on r.dev_repo_id = d.id 
where user_id = #{userId} 

	and d.project_name = #{projectName}

order by r.id desc

select r.*, d.* from requests r join dev_repo d on r.dev_repo_id = d.id where r.dev_repo_id in ( select d.id from project_member pm join dev_repo d on pm.dev_repo_id = d.id where pm.user_id = #{userId} )
and d.project_name = #{projectName}
order by r.id desc
